import React, { Component } from 'react';
import { connect } from 'react-redux'
import { fetchAuthorization } from '../actions/AuthActions'
import BrowseBar from '../components/BrowseBar'
// import HeaderExampleInverted from '../components/Header'
import TrackList from '../components/TrackList'
import WebPlayer2 from '../components/WebPlayer2'
import { Search, Grid, Header, Segment, Image } from 'semantic-ui-react'
import Sidebar from '../components/NavBar'
import AUXHeader from '../components/AUXHeader'
import TopBar from '../components/TopBar'
import TableTest from '../components/TableTest'




class Room extends Component {

  componentWillMount() {
  // Save url queryString
  const queryString = this.props.location.search
  // Check if it contains a CODE
  // If so, send to backend API
  if (queryString.includes('code')) {
    const code = queryString.split('=')[1]
    // Dispact action!
    this.props.fetchAuthorization(code)
    .then(this.props.history.push("/"))
  } else {
    // If not, redirect to home page
    this.props.history.push("/")
  }
}



  render() {
    return (
      <div >
        <Grid columns='equal'>
          <Grid.Column width={3}>
            <Sidebar />
          </Grid.Column>
          <Grid.Column width={13}>
            <Grid.Row>
                <WebPlayer2 />
            </Grid.Row>
            <Grid.Row>
                <TableTest />
            </Grid.Row>



          </Grid.Column>
        </Grid>
      </div>
    );
  }
}

const mapDispatchToProps = {
  fetchAuthorization
}


export default connect(null, mapDispatchToProps)(Room)
