import React, { Component } from 'react';
import { Search, Grid, Header, Segment } from 'semantic-ui-react'


class BrowseBar extends Component {
  componentWillMount() {
  this.resetComponent()
}

resetComponent = () => this.setState({ isLoading: false, results: [], value: '' })




  render() {
    return (
      <div>
      <div className="ui segment">
              <form  className="ui form">
                <div  className="field">
                  <label>BrowseArtist</label>
                  <input type="text" value={this.state.value}  onChange={(e)=> {this.setState({value: e.target.value}, () => {this.props.onBrowseChange(this.state.value)})}} />
                </div>
              </form>
            </div>
            <Grid>
              <Grid.Column width={6}>
                <Search


                  results={this.props.appState.browseResults}
                  value={this.state.term}
                  {...this.props}
                />
              </Grid.Column>
              <Grid.Column width={10}>
                <Segment>

                </Segment>
              </Grid.Column>
            </Grid>
          </div>

    );
  }
}

export default BrowseBar;
