const defaultState =  {
  isLoading: false,
  results: [],
  value: ''
}


function reducer(state=defaultState, action){
  console.log(action)
  switch (action.type) {
    case "CHOOSE_PLAYER":
        return {...state, selectedPlayer: action.payload}
      break;
    default:
      return state
  }
}

export default reducer
