import {
  UPDATE_AUTHORIZATION,
  LOGOUT_USER
} from './actions/types'

const defaultState =  {
  isLoading: false,
  results: [],
  value: '',
  isLoggedIn: false,
  user: {spotify_url: null, display_name: null, uri: null, access_token: null, profile_image: null, username: null, user_id: null},
  error: false
}


function reducer(state=defaultState, action){
  console.log(action)
  switch (action.type) {
    case "UPDATE_TRACK_RESULTS":
        return {...state, results: action.payload}
      break;
      case (UPDATE_AUTHORIZATION):
            return Object.assign({}, state, {user: action.payload.user, isLoggedIn: true})
      case (LOGOUT_USER):
            localStorage.removeItem('jwt')
            return Object.assign({}, state, {
              isLoggedIn: false,
              user: {spotify_id: null, display_name: null, url: null, img_url: null}
            })
    default:
      return state
  }
}

export default reducer
