import React, { Component } from 'react';
import { Search, Grid, Header, Segment } from 'semantic-ui-react'


class BrowseBar extends Component {
  state = {
    term: '',
  }



  render() {
    return (
      <div>
      <div className="ui segment">
              <form  className="ui form">
                <div  className="field">
                  <label>BrowseArtist</label>
                  <input type="text" value={this.state.term}  onChange={(e)=> {this.setState({term: e.target.value}, () => {this.props.onBrowseChange(this.state.term)})}} />
                </div>
              </form>
            </div>
            <Grid>
              <Grid.Column width={6}>
                <Search
                  loading={isLoading}
                  onResultSelect={this.handleResultSelect}
                  onSearchChange={_.debounce(this.handleSearchChange, 500, { leading: true })}
                  results={this.props.appState}
                  value={this.state.term}
                  {...this.props}
                />
              </Grid.Column>
              <Grid.Column width={10}>
                <Segment>
                  <Header>State</Header>
                  <pre style={{ overflowX: 'auto' }}>{JSON.stringify(this.state, null, 2)}</pre>
                  <Header>Options</Header>
                  <pre style={{ overflowX: 'auto' }}>{JSON.stringify(source, null, 2)}</pre>
                </Segment>
              </Grid.Column>
            </Grid>
          </div>

    );
  }
}

export default BrowseBar;
