export function currentUser(){
  return (dispatch) => {
    return fetch(`${ApiUrl}auth`, {
      headers: headers()
    })
    .then(response => response.json())
    .then(json => {
      console.log(json)
      if (!json.error){
        return dispatch({type: UPDATE_AUTHORIZATION, payload: {user: json}})
      }
    })
  }
}

export function logoutUser(){
  return {type: LOGOUT_USER}
}
